import { useState, useContext, useEffect } from 'react';
import { Container, Form, Row, Col, Button, FloatingLabel } from 'react-bootstrap';
import { ContextoUsuarioLogado } from '../../../App';
import { gravar, alterar, listarDepartamentos } from '../../../servicos/departamentoService'; // Inclua listarDepartamentos

export default function FormCadDepartamento(props) {
    const [departamento, setDepartamento] = useState(props.departamentoSelecionado || { codigo: 0, nome: "" });
    const [departamentos, setDepartamentos] = useState([]); // Estado para armazenar a lista de departamentos
    const [validado, setValidado] = useState(false);
    const contextoUsuario = useContext(ContextoUsuarioLogado);

    // Função para buscar os departamentos
    useEffect(() => {
        async function carregarDepartamentos() {
            try {
                const resposta = await listarDepartamentos(contextoUsuario.usuarioLogado.token);
                if (resposta.status) {
                    setDepartamentos(resposta.dados); // Atualize com a estrutura correta da resposta
                } else {
                    alert(resposta.mensagem || "Erro ao carregar os departamentos.");
                }
            } catch (erro) {
                alert("Erro ao enviar a requisição: " + erro.message);
            }
        }

        carregarDepartamentos();
    }, [contextoUsuario.usuarioLogado.token]);

    function manipularMudanca(evento) {
        setDepartamento({
            ...departamento,
            [evento.target.name]: evento.target.value
        });
    }

    function manipularSubmissao(evento) {
        const token = contextoUsuario.usuarioLogado.token;
        const formulario = evento.currentTarget;

        if (formulario.checkValidity()) {
            if (!props.modoEdicao) {
                gravar(departamento, token).then((resposta) => {
                    alert(resposta.mensagem);
                    props.setExibirTabela(true);
                }).catch((erro) => {
                    alert(erro.message);
                });
            } else {
                alterar(departamento, token).then((resposta) => {
                    alert("Atualizado com sucesso!");
                    props.setModoEdicao(false);
                    props.setDepartamentoSelecionado({ codigo: 0, nome: "" });
                    setValidado(false);
                }).catch((erro) => {
                    alert(erro.message);
                });
            }
        } else {
            setValidado(true);
        }

        evento.preventDefault();
        evento.stopPropagation();
    }

    return (
        <Container>
            <Form noValidate onSubmit={manipularSubmissao} validated={validado}>
                <Row>
                    <Col>
                        <Form.Group>
                            <FloatingLabel label="Código:" className="mb-3">
                                <Form.Control
                                    type="text"
                                    placeholder="0"
                                    id="codigo"
                                    name="codigo"
                                    onChange={manipularMudanca}
                                    value={departamento.codigo}
                                    disabled
                                />
                            </FloatingLabel>
                            <Form.Control.Feedback type="invalid">
                                Informe o código do departamento!
                            </Form.Control.Feedback>
                        </Form.Group>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Group>
                            <FloatingLabel label="Departamento:" className="mb-3">
                                <Form.Control
                                    type="text"
                                    placeholder="Informe o nome do departamento"
                                    id="nome"
                                    name="nome"
                                    onChange={manipularMudanca}
                                    value={departamento.nome}
                                    required
                                />
                            </FloatingLabel>
                            <Form.Control.Feedback type="invalid">
                                Informe o nome do departamento!
                            </Form.Control.Feedback>
                        </Form.Group>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Group>
                            <FloatingLabel label="Selecione o Departamento" className="mb-3">
                                <Form.Control
                                    as="select"
                                    id="departamentoSelecionado"
                                    name="departamentoSelecionado"
                                    onChange={manipularMudanca}
                                    value={departamento.codigo}
                                >
                                    <option value="">Escolha um departamento</option>
                                    {departamentos.map(dept => (
                                        <option key={dept.codigo} value={dept.codigo}>
                                            {dept.nome}
                                        </option>
                                    ))}
                                </Form.Control>
                            </FloatingLabel>
                        </Form.Group>
                    </Col>
                </Row>
                <Row className='mt-2 mb-2'>
                    <Col md={1}>
                        <Button type="submit">Confirmar</Button>
                    </Col>
                    <Col md={{ offset: 1 }}>
                        <Button onClick={() => {
                            props.setExibirTabela(true);
                        }}>Voltar</Button>
                    </Col>
                </Row>
            </Form>
        </Container>
    );
}
