const urlBase = "http://localhost:4000/funcionario";

// Função para lidar com a resposta da API
async function handleResponse(resposta) {
    const dados = await resposta.json();
    if (!resposta.ok) {
        console.error('Erro na resposta:', dados); // Adicionado para logar a resposta de erro
        throw new Error(dados.message || 'Erro ao processar a requisição.');
    }
    return dados;
}


// Função para gravar um novo funcionário (POST)


// Função para alterar um funcionário existente (PUT)
export async function alterar(funcionario, token) {
    const resposta = await fetch(urlBase, {
        method: "PUT",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
        },
        credentials: 'include',
        body: JSON.stringify(funcionario)
    });
    return await handleResponse(resposta);
}

// Função para excluir um funcionário (DELETE)
export async function excluir(codigoFuncionario, token) {
    const resposta = await fetch(`${urlBase}/${codigoFuncionario}`, {
        method: "DELETE",
        headers: { 
            "Authorization": `Bearer ${token}`
        },
        credentials: 'include'
    });
    return await handleResponse(resposta);
}

// Função para consultar todos os funcionários (GET)
export async function consultarTodos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Authorization": `Bearer ${token}`
        },
        credentials: 'include'
    });
    return await handleResponse(resposta);
}
