const urlBase = "http://localhost:4000/departamento";

export async function gravar(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "POST",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}` // Formato padrão de token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function alterar(departamento, token) {
    const resposta = await fetch(`${urlBase}/${departamento.codigo}`, {
        method: "PUT",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}` 
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function excluir(departamentoCodigo, token) {
    const resposta = await fetch(`${urlBase}/${departamentoCodigo}`, {
        method: "DELETE",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}` 
        },
        credentials: 'include'
    });
    return await resposta.json();
}

export async function consultarTodos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
        },
        credentials: 'include'
    });
    return await resposta.json();
}
