const urlBase = "http://localhost:4000/departamento";

export async function gravar(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "POST",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function alterar(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "PUT",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function excluir(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "DELETE",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function consultarTodos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Authorization": token
        },
        credentials: 'include'
    });
    return await resposta.json();
}

// No arquivo departamentoService.js
export async function listarDepartamentos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Authorization": `Bearer ${token}`  // Adicione o prefixo Bearer, se necessário
        },
        credentials: 'include'
    });

    // Verifique se a resposta é válida
    if (!resposta.ok) {
        throw new Error(`Erro na resposta: ${resposta.statusText}`);
    }

    // Tente converter a resposta para JSON
    try {
        return await resposta.json();
    } catch (erro) {
        throw new Error("Erro ao processar JSON: " + erro.message);
    }
}
