import { useContext, useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { ContextoUsuarioLogado } from "../../App";
import loginImg from '../../assets/imagens/login-icon.jpg';

export default function TelaLogin() {
  const { setUsuarioLogado } = useContext(ContextoUsuarioLogado);
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error("Login falhou");
      }

      const data = await response.json();

      setUsuarioLogado({
        nome: data.usuario.nome, // Ajuste conforme o retorno do backend
        logado: true,
        token: data.token,
      });

    } catch (error) {
      setError("Credenciais inválidas. Tente novamente.");
    }
  };

  return (
    <Container className='w-50 d-flex align-items-center justify-content-center vh-100'>
      <img className='w-25' src={loginImg} alt="Imagem Login" />
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Usuário</Form.Label>
          <Form.Control
            type="text"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Senha</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </Form.Group>

        {error && <p style={{ color: "red" }}>{error}</p>}

        <Button type="submit">Entrar</Button>
      </Form>
    </Container>
  );
}
