const urlBase = "http://localhost:4000/departamento";

export async function gravar(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "POST",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function alterar(departamento, token) {
    const resposta = await fetch(urlBase, {
        method: "PUT",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(departamento)
    });
    return await resposta.json();
}

export async function excluir(codigo, token) {
    try {
        const resposta = await fetch(`/api/departamentos/${codigo}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
            }
        });
        const dados = await resposta.json(); // Certifique-se de que está tentando analisar o JSON
        return dados;
    } catch (erro) {
        console.error("Erro ao excluir o departamento:", erro);
        throw erro;
    }
}


export async function consultarTodos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Authorization": token
        },
        credentials: 'include'
    });
    return await resposta.json();
}
