const urlBase = "http://localhost:4000/funcionario";

export async function gravar(funcionario, token) {
    const resposta = await fetch(urlBase, {
        method: "POST",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(funcionario)
    });
    return await resposta.json();
}

export async function alterar(funcionario, token) {
    const resposta = await fetch(urlBase, {
        method: "PUT",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(funcionario)
    });
    return await resposta.json();
}

export async function excluir(funcionario, token) {
    const resposta = await fetch(urlBase, {
        method: "DELETE",
        headers: { 
            "Content-Type": "application/json",
            "Authorization": token
        },
        credentials: 'include',
        body: JSON.stringify(funcionario)
    });
    return await resposta.json();
}

export async function consultarTodos(token) {
    const resposta = await fetch(urlBase, {
        method: "GET",
        headers: { 
            "Authorization": token
        },
        credentials: 'include'
    });
    return await resposta.json();
}
