import React, { useState, useEffect, useContext } from 'react';
import { Container, Table, Button } from 'react-bootstrap';
import FormCadFuncionarios from './FormCadFuncionarios'; // Importação do componente de formulário
import { listar, excluir } from '../../../servicos/funcionarioService'; // Serviços para listar e excluir funcionários
import { ContextoUsuarioLogado } from '../../../App'; // Contexto para gerenciar o usuário logado

export default function PaginaFuncionarios() {
  const [funcionarios, setFuncionarios] = useState([]);
  const [exibirFormulario, setExibirFormulario] = useState(false);
  const [funcionarioSelecionado, setFuncionarioSelecionado] = useState({
    codigo: 0,
    nome: '',
    dataAdmissao: '',
    cargo: '',
    departamento: ''
  });
  const [modoEdicao, setModoEdicao] = useState(false);
  const contextoUsuario = useContext(ContextoUsuarioLogado);

  useEffect(() => {
    carregarFuncionarios();
  }, []);

  function carregarFuncionarios() {
    const token = contextoUsuario.usuarioLogado.token;
    listar(token)
      .then((resposta) => {
        setFuncionarios(resposta.dados);
      })
      .catch((erro) => {
        alert('Erro ao carregar funcionários: ' + erro.message);
      });
  }

  function selecionarFuncionario(funcionario) {
    setFuncionarioSelecionado(funcionario);
    setModoEdicao(true);
    setExibirFormulario(true);
  }

  function removerFuncionario(codigo) {
    const token = contextoUsuario.usuarioLogado.token;
    if (window.confirm('Tem certeza que deseja excluir este funcionário?')) {
      excluir(codigo, token)
        .then((resposta) => {
          alert(resposta.mensagem);
          carregarFuncionarios(); // Recarregar lista após exclusão
        })
        .catch((erro) => {
          alert('Erro ao excluir funcionário: ' + erro.message);
        });
    }
  }

  return (
    <Container>
      <h1>Gerenciamento de Funcionários</h1>

      {!exibirFormulario ? (
        <>
          <Button onClick={() => setExibirFormulario(true)}>Novo Funcionário</Button>
          <Table striped bordered hover className="mt-4">
            <thead>
              <tr>
                <th>Código</th>
                <th>Nome</th>
                <th>Data de Admissão</th>
                <th>Cargo</th>
                <th>Departamento</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {funcionarios.length > 0 ? (
                funcionarios.map((funcionario) => (
                  <tr key={funcionario.codigo}>
                    <td>{funcionario.codigo}</td>
                    <td>{funcionario.nome}</td>
                    <td>{funcionario.dataAdmissao}</td>
                    <td>{funcionario.cargo}</td>
                    <td>{funcionario.departamento}</td>
                    <td>
                      <Button variant="primary" onClick={() => selecionarFuncionario(funcionario)}>
                        Editar
                      </Button>{' '}
                      <Button variant="danger" onClick={() => removerFuncionario(funcionario.codigo)}>
                        Excluir
                      </Button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6">Nenhum funcionário cadastrado.</td>
                </tr>
              )}
            </tbody>
          </Table>
        </>
      ) : (
        <FormCadFuncionarios
          funcionarioSelecionado={funcionarioSelecionado}
          setFuncionarioSelecionado={setFuncionarioSelecionado}
          setExibirTabela={setExibirFormulario}
          modoEdicao={modoEdicao}
          setModoEdicao={setModoEdicao}
        />
      )}
    </Container>
  );
}
